using Blazored.TextEditor;
using Microsoft.AspNetCore.Components;
using Microsoft.Extensions.Configuration;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Net.Mail;
using System.Text;
using System.Threading.Tasks;
using VendorRegistration.Data.Models;
using VendorRegistration.Models.UploadedFile;
using VendorRegistration.Services.Data.AuthorizedContactsService;
using VendorRegistration.Services.Data.EmailAttachmentsService;
using VendorRegistration.Services.Data.NotificationRecipientsService;
using VendorRegistration.Services.Data.NotificationsHistoryService;
using VendorRegistration.Services.Data.VendorLinksService;

namespace VendorRegistration.Services.FunctionsService.EmailSenderService
{
    public class EmailSenderService
    {
        private VendorLinksService vendorLinksService;
        private NavigationManager navigationManager;
        private AuthorizedContactsService contactService;
        private NotificationsHistoryService notifService;
        private NotificationRecipientsService recipientService;
        private EmailAttachmentsService attachmentsService;
        private VendorDataDbContext _db;
        private IConfiguration _cfg;
        private string imageLink = "";

        public EmailSenderService(VendorLinksService vls, NavigationManager nm, AuthorizedContactsService acs, NotificationsHistoryService nhs, NotificationRecipientsService nr, EmailAttachmentsService ea, VendorDataDbContext db, IConfiguration cfg)
        {
            vendorLinksService = vls;
            navigationManager = nm;
            contactService = acs;
            notifService = nhs;
            recipientService = nr;
            attachmentsService = ea;
            _db = db;
            _cfg = cfg;
        }

        public void SendPasswordResetEmail(Company[] Companies, VendorAccount account, string email, string baseURI, Vendor_Links vendorLinks)
        {
            var company = Companies.FirstOrDefault(company => company.Id == account.CompanyId && company.Completed_Registeration && !company.Is_Deleted);  //get company matching account email and is not deleted
            var accountId = account.Id;
            Guid guid = Guid.NewGuid();
            string randomString = Convert.ToBase64String(guid.ToByteArray());
            randomString = randomString.Replace("=", "").Replace("/", "");

            MailMessage message = new MailMessage();

            message.Subject = "Reset your password";
            message.From = new MailAddress("HHC-MCPHD-Purchasing-Do-Not-Reply@hhcorp.org");
            message.IsBodyHtml = true;
            message.To.Add(new MailAddress($"{email}"));
            message.Body = $"<h2>Password Reset:</h2><p>Hello {company?.Name},</p> <br/><p>Please click on this temporary link below to reset your password: {baseURI}password-reset/{randomString}/{account.Id}</p><br/><p>Thank you,<br><p>Purchasing Administrator<br></p><p>HHC/MCPHD <br></p><p>quotes@hhcorp.org</p>";

            var sClient = new SmtpClient("fakeClient.org");
            sClient.Port = 25;
            sClient.UseDefaultCredentials = false;

            sClient.Send(message);

            if (vendorLinks is not null)
            {
                vendorLinks.AccountId = accountId;
                vendorLinks.Link = $"{baseURI}password-reset/{randomString}/{accountId}";
                vendorLinks.DateLinkSent = DateTime.Now;
                _db.Vendor_Links.Add(vendorLinks);
                _db.SaveChanges();
            }
        }

        public async Task SendNotification(string SubjectLine, string SendNotifSuccess, string SendNotifError, MailMessage message, string QuillContent, BlazoredTextEditor Quill, List<UploadedFile> FileList,
           List<Company> SelectedCompanies, string EmailToPerson, string TemplateEmail, Notifications Notif, Email_Attachments Db_EmailAttach, Notification_Recipient RecipientObject)
        {
            message.Subject = SubjectLine;
            message.From = new MailAddress("HHC-MCPHD-Purchasing-Do-Not-Reply@hhcorp.org");
            message.IsBodyHtml = true;

            QuillContent = await Quill.GetHTML();

            var bytes = Convert.FromBase64String($"{imageLink}");
            var contents = new MemoryStream(bytes);

            foreach (var file in FileList)
            {
                message.Attachments.Add(new Attachment(file.Base64String, file.IBrowser.ContentType));
            }

            foreach (var company in SelectedCompanies)
            {
                var CompanyContacts = contactService.GetContactsFromCompanyId(company.Id);

                foreach (var person in CompanyContacts)
                {
                    if (person is not null)
                    {
                        EmailToPerson = $"{person.FirstName} {person.LastName},";

                        message.To.Clear();            //So each email is sent to person one at a time: prevents other companies seeing their competitors
                        message.To.Add(new MailAddress($"{person.Email}"));

                    }
                    var Template = QuillContent;

                    TemplateEmail = EmailToPerson + Template;
                    message.Body = TemplateEmail;

                    var sClient = new SmtpClient("fakeClient.ORG");
                    sClient.Port = 25;
                    sClient.UseDefaultCredentials = false;

                    sClient.Send(message);

                    TemplateEmail = "";
                }
                EmailToPerson = "";
            }


            Notif = new Notifications();
            Notif.Title = SubjectLine;
            Notif.Body = "To Recipients, <br/><br/>" + QuillContent;

            Notif.Date_Sent = DateTime.Now;
            Notif.Created_By = "test@hhcorp.org";

            _db.NotificationsList.Add(Notif);
            _db.SaveChanges();


            foreach (var file in FileList)
            {
                Db_EmailAttach = new Email_Attachments();
                Db_EmailAttach.Notifications_Id = Notif.Id;
                Db_EmailAttach.File_Name = file.Name;
                Db_EmailAttach.Base_64_String = file.Base64String;
                Db_EmailAttach.Content_Type = file.IBrowser.ContentType;

                _db.Email_Attachments.Add(Db_EmailAttach);
            }

            foreach (var company in SelectedCompanies)
            {
                RecipientObject = new Notification_Recipient();
                RecipientObject.NotificationsId = Notif.Id;
                RecipientObject.CompanyId = company.Id;
                RecipientObject.CompanyName = company.Name;

                _db.Notification_Recipients.Add(RecipientObject);
            }
            _db.SaveChanges();
        }
    }
}
